import React, { useState, useEffect } from 'react';
import axios from 'axios';

function Product() {
    const url = "http://127.0.0.1:5000/product";
    const [products, setProducts] = useState([]);

    const AddRecord=()=>
    {
        axios.post(url,products).then((result)=>{
