import '../node_modules/bootstrap/dist/css/bootstrap.min.css'
import React, { useState, useEffect } from 'react';
import axios from 'axios';

function Product() {
    const url = "http://127.0.0.1:5000/product";
    const [products, setProducts] = useState([]);
const [product, setProduct] = useState({ productid: "", producttitle: "", price: "", stock: "" });

const OnTextChange = (args) => {
    var product1={...product};
    product1[args.target.name]=args.target.value;
    setProduct(product1);
};

const AddRecord = () => {
    axios.post(url, product).then((result) => {
        if (result.data.affectedRows !== undefined && result.data.affectedRows > 0) {
            FetchRecord();
        }
    });
};

    const FetchRecord=()=>{
        debugger
        axios.get(url).then((result)=>{
            setProducts(result.data)
        })
    }
    useEffect(() => {
        FetchRecord();
      }, []);

    //   useEffect(() => {
    //     console.log("Products state updated!");
    //   }, [products]);


    return (
        <div className='container'>
            <div className='table-responsive'>
                <table className='table table-bordered'>
                    <tbody>
                        {/* <tr>
                            <td>Product ID</td>
                            <td>
                                <input
                                    name="productid"
                                    value={product.productid}
                                    onChange={OnTextChange}
                                />
                            </td>
                        </tr> */}
                        <tr>
                            <td>Product Title</td>
                            <td>
                                <input
                                    name="producttitle"
                                    value={product.producttitle}
                                    onChange={OnTextChange}
                                />
                            </td>
                        </tr>
                        <tr>
                            <td>Price</td>
                            <td>
                                <input
                                    name="price"
                                    value={product.price}
                                    onChange={OnTextChange}
                                />
                            </td>
                        </tr>
                        <tr>
                            <td>Stock</td>
                            <td>
                                <input
                                    name="stock"
                                    value={product.stock}
                                    onChange={OnTextChange}
                                />
                            </td>
                        </tr>
                        <tr>
                            <td></td>
                            <td>
                                <button className="btn btn-primary" onClick={AddRecord}>
                                    Add Record
                                </button>
                            </td>
                        </tr>
                    </tbody>
                </table>
            </div>
            <div className='table-responsive'>
                <table className='table table-bordered'>
                    <thead>
                        <tr>
                            <th>ProductID</th>
                            <th>ProductTitle</th>
                            <th>Price</th>
                            <th>Stock</th>
                        </tr>
                    </thead>
                    <tbody>
                        {products.map((product) => (
                            <tr key={product.productid}>
                                <td>{product.productid}</td>
                                <td>{product.producttitle}</td>
                                <td>{product.price}</td>
                                <td>{product.stock}</td>
                            </tr>
                        ))}
                    </tbody>
                </table>
            </div>
        </div>
    );
}


export default Product;
