import '../node_modules/bootstrap/dist/css/bootstrap.min.css';
import React, { useState } from 'react';
import axios from 'axios';
import { useHistory } from 'react-router-dom'; // Import useHistory from react-router-dom

function AddProduct() {
    const url = "http://127.0.0.1:5000/product";
    const [product, setProduct] = useState({ producttitle: "", price: "", stock: "" });
    const history = useHistory(); // Get the history object from React Router

    const OnTextChange = (args) => {
        var product1 = { ...product };
        product1[args.target.name] = args.target.value;
        setProduct(product1);
    };

    const AddRecord = () => {
        axios.post(url, product).then((result) => {
            if (result.data.affectedRows !== undefined && result.data.affectedRows > 0) {
                // Redirect back to the Product page after adding a record
                history.push('/product');
            }
        });
    };

    return (
        <div className='container'>
            <div className='table-responsive'>
                <table className='table table-bordered'>
                    <tbody>
                        <tr>
                            <td>Product Title</td>
                            <td>
                                <input
                                    name="producttitle"
                                    value={product.producttitle}
                                    onChange={OnTextChange}
                                />
                            </td>
                        </tr>
                        <tr>
                            <td>Price</td>
                            <td>
                                <input
                                    name="price"
                                    value={product.price}
                                    onChange={OnTextChange}
                                />
                            </td>
                        </tr>
                        <tr>
                            <td>Stock</td>
                            <td>
                                <input
                                    name="stock"
                                    value={product.stock}
                                    onChange={OnTextChange}
                                />
                            </td>
                        </tr>
                        <tr>
                            <td></td>
                            <td>
                                <button className="btn btn-primary" onClick={AddRecord}>
                                    Add Record
                                </button>
                            </td>
                        </tr>
                    </tbody>
                </table>
            </div>
        </div>
    );
}

export default AddProduct;
