 import '../node_modules/bootstrap/dist/css/bootstrap.min.css'
import React, { useState, useEffect } from 'react';
import axios from 'axios';

function Product() {
    const url = "http://127.0.0.1:5000/product";
    const [products, setProducts] = useState([]);
    const [AddRecords, setAddRecords] = useState(false);
    const [product ,setProduct]=useState({producttitle:"",price:"",stock:""});
    const AddProduct = () => {
        setAddRecords(!AddRecords);
    };

    const RemovedRecord = (productid) => {
        const confirmation = window.confirm("Are you sure you want to delete this product?");
        if (confirmation) {
            axios.delete(url + "/" + productid).then((result) => {
                if (result.data.affectedRows !== undefined && result.data.affectedRows > 0) {
                    FetchRecord();
                }
            });
        }
    };

    const FetchRecord = () => {
        axios.get(url).then((result) => {
            setProducts(result.data);
        });
    };

    const handleInputChange = (e) => {
        const { name, value } = e.target;
        setNewProduct({ ...newProduct, [name]: value });
    };

    const handleAddRecord = () => {
        axios.post(url, newProduct).then((result) => {
            if (result.data.affectedRows !== undefined && result.data.affectedRows > 0) {
                setNewProduct({ producttitle: "", price: "", stock: "" }); // Reset the input fields
                FetchRecord();
                setAddRecords(false); // Switch back to displaying the product list
            }
        });
    };

    useEffect(() => {
        FetchRecord();
    }, []);

    return (
        <div className='container'>
            {!AddRecords ? (
                <div className='table-responsive'>
                    <table className='table table-bordered'>
                        <thead>
                            <tr>
                                <th>ProductID</th>
                                <th>ProductTitle</th>
                                <th>Price</th>
                                <th>Stock</th>
                                <th>Actions</th>
                            </tr>
                        </thead>
                        <tbody>
                            {products.map((product) => (
                                <tr
                                    key={product.productid}
                                    className={product.stock === 0 ? 'table-danger' : 'table-info'}
                                >
                                    <td>{product.productid}</td>
                                    <td>{product.producttitle}</td>
                                    <td>{product.price}</td>
                                    <td>{product.stock === 0 ? 'Not Available' : product.stock}</td>
                                    <td>
                                        <button className="btn btn-danger" onClick={() => RemovedRecord(product.productid)}>Delete</button>
                                    </td>
                                </tr>
                            ))}
                        </tbody>
                    </table>
                    <button className="btn btn-primary" onClick={AddProduct}>
                        Add Product
                    </button>
                </div>
            ) : (
                <div>
                    <h2>Add Product</h2>
                    <table className='table table-bordered'>
                        <tbody>
                            <tr>
                                <td>Product Title:</td>
                                <td>
                                    <input
                                        type="text"
                                        name="producttitle"
                                        value={newProduct.producttitle}
                                        onChange={handleInputChange}
                                    />
                                </td>
                            </tr>
                            <tr>
                                <td>Price:</td>
                                <td>
                                    <input
                                        type="text"
                                        name="price"
                                        value={newProduct.price}
                                        onChange={handleInputChange}
                                    />
                                </td>
                            </tr>
                            <tr>
                                <td>Stock:</td>
                                <td>
                                    <input
                                        type="text"
                                        name="stock"
                                        value={newProduct.stock}
                                        onChange={handleInputChange}
                                    />
                                </td>
                            </tr>
                            <tr>
                                <td></td>
                                <td>
                                    <button className="btn btn-primary" onClick={handleAddRecord}>
                                        Add Record
                                    </button>
                                    <button className="btn btn-secondary" onClick={AddProduct}>
                                        Cancel
                                    </button>
                                </td>
                            </tr>
                        </tbody>
                    </table>
                </div>
            )}
        </div>
    );
}

export default Product;
