import axios from "axios";
import { useEffect, useState } from "react";
import "../node_modules/bootstrap/dist/css/bootstrap.min.css";
import "./common.css";
function Productlist() {
	const [prods, setProds] = useState([]);
	const [prod, setProd] = useState({
		productid: "",
		producttitle: "",
		price: "",
		stock: "",
	});
	const [message, setMessage] = useState("");

	const url = "http://127.0.0.1:5000/product";

	const FetchRecords = () => {
		axios.get(url).then((result) => {
			//debugger;
			setProds(result.data);
		});
		// console.log(result.data);
	};
	const isValidDateValue = (stock) => {
		if (stock === 0) {
			return "Not available";
		} else {
			return stock;
		}
	};
	useEffect(() => {
		FetchRecords();
	}, []);
	useEffect(() => {
		console.log("Some State change did update the UI");
	}, [prods, prod, message]);
	//
	const Remove = (productid) => {
		if (window.confirm("Do you Really want to delete this row " + productid)) {
			// txt = "You pressed OK!";
			axios.delete(url + "/" + productid).then((result) => {
				if (
					result.data.affectedRows !== undefined &&
					result.data.affectedRows > 0
				) {
					//ShowMessage("Record removed successfully!");
					FetchRecords();
				}
			});
		} else {
			// let txt = "You"
		}
	};
	const Update = () => {
		const updateurl = url + "/" + prod.productid;
		console.log(updateurl);
		console.log(prod);
		axios.put(updateurl, prod).then((result) => {
			FetchRecords();
		});
	};
	const OnTextChange = (args) => {
		var prod1 = { ...prod };
		prod1[args.target.name] = args.target.value;
		setProd(prod1);
	};
	const Edit = (productid) => {
		for (var i = 0; i < prods.length; i++) {
			if (prods[i].productid === productid) {
				var copyOfProdsToEdit = { ...prods[i] };
				setProd(copyOfProdsToEdit);
			}
		}
	};
	const OnTextChangeValidation = (args) => {
		var prod1 = { ...prod };
		if (
			prod1.productid === 0 ||
			prod1.producttitle === "" ||
			prod1.price === "" ||
			prod1.stock === ""
		) {
			debugger;
